function [th,r] = cart2pol_my(x,y)

[th, r] = cart2pol(x,y);

th = th + 2*pi*(th < 0);
end

%% Lshape solution

function [out] = lshape_solution(x,y)
[th,r] = cart2pol_my(x,y);
out = (r.^(2/3)).* sin(2/3*th);
end

function [out] = lshape_ugrad(x,y)

[th,r] = cart2pol_my(x,y);
out = [...
    2*( x.* sin( 2/3*th )  - y.* cos( 2/3*th ))./...
    (3* r.^(4/3)),...
    2*( y.* sin( 2/3*th )  + x.* cos( 2/3*th ))./...
    (3* r.^(4/3)) ...
    ];
end

%% crack solution

function [out] = crack_solution(x,y)
[th,r] = cart2pol_my(x,y);
out = (r.^(1/2)).* sin(th/2) - 1/4* r.^2;
end

function [out] = crack_ugrad(x,y)

[th,r] = cart2pol_my(x,y);
out = [...
    cos( th ) .* (sin(1/2* th) * .5 .* r.^(-1/2) - 1/2* r) ...
    - 1./r .* sin( th ) .* (.5 * r.^(1/2) .* cos( th/2 )), ...
    ...
    sin( th ) .* (sin(1/2* th) * .5 .* r.^(-1/2) - 1/2* r) ...
    + 1./r .* cos( th ) .* (.5 * r.^(1/2) .* cos( th/2 ))
    ];

end

%% inhomogeneous solution

function [out] = inhomogeneous_solution(x,y)
[th,r] = cart2pol_my(x,y);
[alpha, a, b] = inhomogeneous_solutionparameters( th );

out = (r.^(alpha)).*(a.* sin(alpha*th) + b.* cos(alpha*th));
end

function [out] = inhomogeneous_ugrad(x,y)
[th,r] = cart2pol_my(x,y);
[alpha, a, b] = inhomogeneous_solutionparameters( th );

dudr = alpha* r.^(alpha-1).* (a.* sin(alpha*th) + b.* cos(alpha*th));
dudth = (r.^(alpha)).* alpha.* (a.* cos(alpha*th) - b.* sin(alpha*th));

dudx = (cos(th).*dudr - (sin(th)./r) .* dudth);
dudy = (sin(th).*dudr + (cos(th)./r) .* dudth);

out = [dudx, dudy];

end

function [alpha, a, b] = inhomogeneous_solutionparameters( th )

a = zeros(size(th));
b = a;

alpha = 0.12690207;

for i = 1:length(th)
    
    if th(i) <= pi/2,
        a(i) =  0.10000000; b(i) =  1;
    elseif (th(i) > pi/2) && (th(i) <= pi),
        a(i) = -9.60396040; b(i) =  2.96039604;
    elseif (th(i) > pi) && (th(i) <= 3/2*pi),
        a(i) = -0.48035487; b(i) = -0.88275659;
    else
        a(i) =  7.70156488; b(i) = -6.45646175;
    end
end

end



