dmjudth = zeros(size(th));

for i = 1:length(th)
    
    if th(i) <= pi/2,
        mju(i) = cos(((pi/2) - sigma)*gamma) * cos((th(i) - (pi/2) + rho)*gamma);
        dmjudth(i) = cos(((pi/2) - sigma)*gamma) * -sin((th(i) - (pi/2) + rho)*gamma) * gamma;
    elseif (th(i) > pi/2) && (th(i) <= pi),
        mju(i) = cos(rho*gamma) * cos((th(i) - pi + sigma)*gamma);
        dmjudth(i) = cos(rho*gamma) * -sin((th(i) - pi + sigma)*gamma) * gamma;
    elseif (th(i) > pi) && (th(i) <= 3/2*pi),
        mju(i) = cos(sigma*gamma) * cos((th(i) - pi - rho)*gamma);
        dmjudth(i) = cos(sigma*gamma) * -sin((th(i) - pi - rho)*gamma) * gamma;
    else
        mju(i) = cos(((pi/2) - rho)*gamma) * cos((th(i) - (3*pi/2) - sigma)*gamma);
        dmjudth(i) = cos(((pi/2) - rho)*gamma) * -sin((th(i) - (3*pi/2) - sigma)*gamma) * gamma;
    end
end

end
